// @ts-nocheck
import React, { createRef, Component } from 'react';
import {
  Dimensions,
  Keyboard,
  TextInput,
  ScrollView,
  Platform,
  EmitterSubscription,
} from 'react-native';
import Random from './random';
import { CustomStep, OptionsStep, TextStep } from './steps/steps';
import schema from './schemas/schema';
import ChatBotContainer from './ChatBotContainer';
import InputView from './InputView';
import Footer from './Footer';
import Button from './Button';
import ButtonText from './ButtonText';

const { height, width } = Dimensions.get('window');

const defaultProps: Partial<Props> = {
  avatarStyle: {},
  avatarWrapperStyle: {},
  botBubbleColor: '#6E48AA',
  botDelay: 1000,
  botFontColor: '#fff',
  bubbleStyle: {},
  optionStyle: {},
  optionBubbleColor: '#6E48AA',
  optionFontColor: '#fff',
  optionElementStyle: {},
  contentStyle: {},
  customStyle: {},
  customDelay: 1000,
  customLoadingColor: '#4a4a4a',
  className: '',
  footerStyle: {},
  handleEnd: undefined,
  hideBotAvatar: false,
  hideUserAvatar: false,
  inputAttributes: {},
  inputStyle: {},
  keyboardVerticalOffset: Platform.OS === 'ios' ? 44 : 0,
  placeholder: 'Type the message ...',
  headerComponent: undefined,
  style: {},
  submitButtonStyle: {},
  submitButtonContent: 'SEND',
  userBubbleStyle: {},
  userBubbleColor: '#fff',
  userDelay: 1000,
  userFontColor: '#4a4a4a',
  botAvatar:
    'data:image/png;base64,',
  userAvatar:
    'data:image/png;base64,',
  scrollViewProps: {},
};

interface Props {
  avatarStyle?: {};
  avatarWrapperStyle?: {};
  botAvatar?: string;
  botBubbleColor?: string;
  botDelay?: number;
  botFontColor?: string;
  bubbleStyle?: {};
  optionStyle?: {};
  optionBubbleColor?: string;
  optionFontColor?: string;
  optionElementStyle?: {};
  contentStyle?: {};
  customStyle?: {};
  customDelay?: number;
  customLoadingColor?: string;
  className?: string;
  handleEnd?: Function;
  headerComponent?: Component; // PropTypes.element,
  hideBotAvatar?: boolean;
  hideUserAvatar?: boolean;
  footerStyle?: {};
  inputAttributes?: {}; // PropTypes.objectOf(PropTypes.any),
  inputStyle?: {};
  keyboardVerticalOffset?: number;
  placeholder?: string;
  steps: any[];
  style?: {};
  submitButtonStyle?: {};
  submitButtonContent?: string | Component;
  // PropTypes.oneOfType([
  //   PropTypes.string,
  //   PropTypes.element,
  // ]),
  userAvatar?: string;
  userBubbleStyle?: {};
  userBubbleColor?: {};
  userDelay?: number;
  userFontColor?: string;
  scrollViewProps?: {};
}

interface State {
  renderedSteps: string[];
  previousSteps: string[];
  currentStep: {};
  previousStep: {};
  steps: {};
  editable: boolean;
  inputValue: string;
  inputInvalid: boolean;
  defaultUserSettings: {};
}

class ChatBot extends Component<Props, State> {
  public static defaultProps: Partial<Props> = defaultProps;

  public readonly state = {
    renderedSteps: [],
    previousSteps: [],
    currentStep: {},
    previousStep: {},
    steps: {},
    editable: false,
    inputValue: '',
    inputInvalid: false,
    defaultUserSettings: {},
  };

  private keyboardDidShowListener: EmitterSubscription;
  private keyboardDidHideListener: EmitterSubscription;
  private scrollView = createRef<ScrollView>();
  private inputRef = createRef<TextInput>();

  constructor(props: Props) {
    super(props);

    // instead of using a timeout on input focus/blur we can listen for the native keyboard events
    this.keyboardDidShowListener = Keyboard.addListener(
      'keyboardDidShow',
      this.setScrollViewScrollToEnd
    );
    this.keyboardDidHideListener = Keyboard.addListener(
      'keyboardDidHide',
      this.setScrollViewScrollToEnd
    );
  }

  public componentWillMount() {
    const {
      botDelay,
      botAvatar,
      botBubbleColor,
      botFontColor,
      customDelay,
      customLoadingColor,
      userDelay,
      userAvatar,
      userBubbleColor,
      userFontColor,
      optionBubbleColor,
      optionFontColor,
    } = this.props;
    const steps = {};

    const defaultBotSettings = {
      delay: botDelay,
      avatar: botAvatar,
      bubbleColor: botBubbleColor,
      fontColor: botFontColor,
      optionBubbleColor,
      optionFontColor,
    };
    const defaultUserSettings = {
      delay: userDelay,
      avatar: userAvatar,
      bubbleColor: userBubbleColor,
      fontColor: userFontColor,
    };
    const defaultCustomSettings = {
      delay: customDelay,
      loadingColor: customLoadingColor,
    };

    for (let i = 0, len = this.props.steps.length; i < len; i += 1) {
      const step = this.props.steps[i];
      let settings = {};

      if (step.user) {
        settings = defaultUserSettings;
      } else if (step.message || step.asMessage || step.options) {
        settings = defaultBotSettings;
      } else if (step.component) {
        settings = defaultCustomSettings;
      }

      steps[step.id] = Object.assign({}, settings, schema.parse(step));
    }

    schema.checkInvalidIds(steps);

    const firstStep = this.props.steps[0];

    if (firstStep.message) {
      const { message } = firstStep;
      firstStep.message = typeof message === 'function' ? message() : message;
      steps[firstStep.id].message = firstStep.message;
    }

    const currentStep = firstStep;
    const renderedSteps = [steps[currentStep.id]];
    const previousSteps = [steps[currentStep.id]];

    this.setState({
      defaultUserSettings,
      steps,
      currentStep,
      renderedSteps,
      previousSteps,
    });
  }

  public componentWillUnmount() {
    this.keyboardDidShowListener.remove();
    this.keyboardDidHideListener.remove();
  }

  onButtonPress = () => {
    const {
      renderedSteps,
      previousSteps,
      inputValue,
      defaultUserSettings,
    } = this.state;
    let { currentStep } = this.state;

    const isInvalid = currentStep.validator && this.checkInvalidInput();

    if (!isInvalid) {
      const step = {
        message: inputValue,
        value: inputValue,
      };

      currentStep = Object.assign({}, defaultUserSettings, currentStep, step);

      renderedSteps.push(currentStep);
      previousSteps.push(currentStep);

      this.setState({
        currentStep,
        renderedSteps,
        previousSteps,
        editable: false,
        inputValue: '',
      });
    }
  };

  getStepMessage = message => {
    const { previousSteps } = this.state;
    const lastStepIndex =
      previousSteps.length > 0 ? previousSteps.length - 1 : 0;
    const steps = this.generateRenderedStepsById();
    const previousValue = previousSteps[lastStepIndex].value;
    return typeof message === 'function'
      ? message({ previousValue, steps })
      : message;
  };

  getTriggeredStep = (trigger, value) => {
    const steps = this.generateRenderedStepsById();
    return typeof trigger === 'function' ? trigger({ value, steps }) : trigger;
  };

  // setContentRef = (c) => {
  //   this.scrollView = c;
  // };

  // setInputRef = (c) => {
  //   this.inputRef = c;
  // };

  setScrollViewScrollToEnd = () => {
    this.scrollView.current.scrollToEnd();
  };

  handleEnd = () => {
    const { previousSteps } = this.state;

    const renderedSteps = previousSteps.map(step => {
      const { id, message, value, metadata } = step;
      return { id, message, value, metadata };
    });

    const steps = [];

    for (let i = 0, len = previousSteps.length; i < len; i += 1) {
      const { id, message, value, metadata } = previousSteps[i];
      steps[id] = { id, message, value, metadata };
    }

    const values = previousSteps
      .filter(step => step.value)
      .map(step => step.value);

    if (this.props.handleEnd) {
      this.props.handleEnd({ renderedSteps, steps, values });
    }
  };

  triggerNextStep = data => {
    const {
      renderedSteps,
      previousSteps,
      steps,
      defaultUserSettings,
    } = this.state;
    let { currentStep, previousStep } = this.state;
    const isEnd = currentStep.end;

    if (data && data.value) {
      currentStep.value = data.value;
    }
    if (data && data.trigger) {
      currentStep.trigger = this.getTriggeredStep(data.trigger, data.value);
    }

    if (isEnd) {
      this.handleEnd();
    } else if (currentStep.options && data) {
      const option = currentStep.options.filter(o => o.value === data.value)[0];
      const trigger = this.getTriggeredStep(option.trigger, currentStep.value);
      delete currentStep.options;

      currentStep = Object.assign(
        {},
        currentStep,
        option,
        defaultUserSettings,
        {
          user: true,
          message: option.label,
          trigger,
        }
      );

      renderedSteps.pop();
      previousSteps.pop();
      renderedSteps.push(currentStep);
      previousSteps.push(currentStep);

      this.setState({
        currentStep,
        renderedSteps,
        previousSteps,
      });
    } else if (currentStep.trigger) {
      const isReplace = currentStep.replace && !currentStep.option;

      if (isReplace) {
        renderedSteps.pop();
      }

      const trigger = this.getTriggeredStep(
        currentStep.trigger,
        currentStep.value
      );
      let nextStep = Object.assign({}, steps[trigger]);

      if (nextStep.message) {
        nextStep.message = this.getStepMessage(nextStep.message);
      } else if (nextStep.update) {
        const updateStep = nextStep;
        nextStep = Object.assign({}, steps[updateStep.update]);

        if (nextStep.options) {
          for (let i = 0, len = nextStep.options.length; i < len; i += 1) {
            nextStep.options[i].trigger = updateStep.trigger;
          }
        } else {
          nextStep.trigger = updateStep.trigger;
        }
      }

      nextStep.key = Random(24);

      previousStep = currentStep;
      currentStep = nextStep;

      if (nextStep.user) {
        this.setState({ editable: true });
        this.inputRef.current.focus();
      } else {
        renderedSteps.push(nextStep);
        previousSteps.push(nextStep);
      }

      this.setState({
        renderedSteps,
        previousSteps,
        currentStep,
        previousStep,
      });

      Keyboard.dismiss();
    }
  };

  generateRenderedStepsById = () => {
    const { previousSteps } = this.state;
    const steps = {};

    for (let i = 0, len = previousSteps.length; i < len; i += 1) {
      const { id, message, value, metadata } = previousSteps[i];
      steps[id] = { id, message, value, metadata };
    }

    return steps;
  };

  isLastPosition = step => {
    const { renderedSteps } = this.state;
    const { length } = renderedSteps;
    const stepIndex = renderedSteps.map(s => s.key).indexOf(step.key);

    if (length <= 1 || stepIndex + 1 === length) {
      return true;
    }

    const nextStep = renderedSteps[stepIndex + 1];
    const hasMessage = nextStep.message || nextStep.asMessage;

    if (!hasMessage) {
      return true;
    }

    const isLast = step.user !== nextStep.user;
    return isLast;
  };

  isFirstPosition = step => {
    const { renderedSteps } = this.state;
    const stepIndex = renderedSteps.map(s => s.key).indexOf(step.key);

    if (stepIndex === 0) {
      return true;
    }

    const lastStep = renderedSteps[stepIndex - 1];
    const hasMessage = lastStep.message || lastStep.asMessage;

    if (!hasMessage) {
      return true;
    }

    const isFirst = step.user !== lastStep.user;
    return isFirst;
  };

  handleKeyPress = event => {
    if (event.nativeEvent.key === 'Enter') {
      this.onButtonPress();
    }
  };

  checkInvalidInput = () => {
    const { currentStep, inputValue } = this.state;
    const result = currentStep.validator(inputValue);
    const value = inputValue;

    if (typeof result !== 'boolean' || !result) {
      this.setState({
        inputValue: result.toString(),
        inputInvalid: true,
        editable: false,
      });

      setTimeout(() => {
        this.setState({
          inputValue: value,
          inputInvalid: false,
          editable: true,
        });
        this.inputRef.current.focus();
      }, 2000);

      return true;
    }

    return false;
  };

  renderStep = (step, index: number) => {
    const { renderedSteps, previousSteps } = this.state;
    const {
      avatarStyle,
      avatarWrapperStyle,
      bubbleStyle,
      userBubbleStyle,
      optionStyle,
      optionElementStyle,
      customStyle,
      customDelay,
      hideBotAvatar,
      hideUserAvatar,
    } = this.props;
    const { options, component, asMessage } = step;
    const steps = {};
    const stepIndex = renderedSteps.map(s => s.id).indexOf(step.id);
    const previousStep = stepIndex > 0 ? renderedSteps[index - 1] : {};

    for (let i = 0, len = previousSteps.length; i < len; i += 1) {
      const ps = previousSteps[i];

      steps[ps.id] = {
        id: ps.id,
        message: ps.message,
        value: ps.value,
      };
    }

    if (component && !asMessage) {
      return (
        <CustomStep
          key={index}
          delay={customDelay}
          step={step}
          steps={steps}
          style={customStyle}
          previousStep={previousStep}
          triggerNextStep={this.triggerNextStep}
        />
      );
    }

    if (options) {
      return (
        <OptionsStep
          key={index}
          step={step}
          triggerNextStep={this.triggerNextStep}
          optionStyle={optionStyle || bubbleStyle}
          optionElementStyle={optionElementStyle || bubbleStyle}
        />
      );
    }

    return (
      <TextStep
        key={index}
        step={step}
        steps={steps}
        previousValue={previousStep.value}
        triggerNextStep={this.triggerNextStep}
        avatarStyle={avatarStyle}
        avatarWrapperStyle={avatarWrapperStyle}
        bubbleStyle={bubbleStyle}
        userBubbleStyle={userBubbleStyle}
        hideBotAvatar={hideBotAvatar}
        hideUserAvatar={hideUserAvatar}
        isFirst={this.isFirstPosition(step)}
        isLast={this.isLastPosition(step)}
      />
    );
  };

  public render() {
    const {
      currentStep,
      editable,
      inputInvalid,
      inputValue,
      renderedSteps,
    } = this.state;
    const {
      botBubbleColor,
      botFontColor,
      contentStyle,
      footerStyle,
      headerComponent,
      inputAttributes,
      inputStyle,
      keyboardVerticalOffset,
      placeholder,
      style,
      submitButtonStyle,
      submitButtonContent,
      scrollViewProps,
    } = this.props;

    const styles = {
      input: {
        borderWidth: 0,
        color: inputInvalid ? '#E53935' : '#4a4a4a',
        fontSize: 14,
        opacity: !editable && !inputInvalid ? 0.5 : 1,
        paddingRight: 16,
        paddingLeft: 16,
        height: 50,
        width: width - 80,
      },
      content: {
        height: height - 50,
        backgroundColor: '#eee',
      },
    };

    const textInputStyle = Object.assign({}, styles.input, inputStyle);
    const scrollViewStyle = Object.assign({}, styles.content, contentStyle);
    const platformBehavior = Platform.OS === 'ios' ? 'padding' : 'height';
    const inputAttributesOverride =
      currentStep.inputAttributes || inputAttributes;

    return (
      <ChatBotContainer style={style}>
        {!!headerComponent && headerComponent}
        <ScrollView
          style={scrollViewStyle}
          ref={this.scrollView}
          onContentSizeChange={this.setScrollViewScrollToEnd}
          {...scrollViewProps}
        >
          {renderedSteps.map(this.renderStep)}
        </ScrollView>
        <InputView
          behavior={platformBehavior}
          keyboardVerticalOffset={keyboardVerticalOffset}
        >
          <Footer
            style={footerStyle}
            disabled={!editable}
            invalid={inputInvalid}
            color={botBubbleColor}
          >
            <TextInput
              type="textarea"
              style={textInputStyle}
              placeholder={placeholder}
              ref={this.inputRef}
              onKeyPress={this.handleKeyPress}
              onChangeText={text => this.setState({ inputValue: text })}
              value={inputValue}
              underlineColorAndroid="transparent"
              invalid={inputInvalid}
              editable={editable}
              {...inputAttributesOverride}
            />
            <Button
              style={submitButtonStyle}
              disabled={!editable}
              onPress={this.onButtonPress}
              invalid={inputInvalid}
              backgroundColor={botBubbleColor}
            >
              <ButtonText invalid={inputInvalid} fontColor={botFontColor}>
                {submitButtonContent}
              </ButtonText>
            </Button>
          </Footer>
        </InputView>
      </ChatBotContainer>
    );
  }
}

export default ChatBot;
